mapper.select:
SELECT TB.ORDER_ID , TB.ORDER_ORDERING_DATE , TB.ORDER_DEAD_LINE_DATE , TB.ORG_NAME , TB.ORDER_STYLE_NOS FROM ( SELECT TOR.ORDER_ID , TOR.ORDER_ORDERING_DATE , TOR.ORDER_DEAD_LINE_DATE , TON.ORG_NAME , GROUP_CONCAT(DISTINCT TPT.PRODUCT_STYLE_NO ORDER BY TPT.PRODUCT_STYLE_NO SEPARATOR ', ') ORDER_STYLE_NOS FROM TB_ORDER TOR LEFT JOIN TB_PRODUCT TPT ON TOR.ORDER_ID = TPT.ORDER_ID LEFT JOIN TB_ORGANIZATION TON ON TON.ORG_ID = TOR.ORDER_ORG_ID WHERE 1=1 AND TOR.USE_YN = 'Y' AND TOR.ORDER_ORG_ID = #{orgId}
AND TOR.ORDER_ORDERING_DATE >= #{startDate}
AND TOR.ORDER_ORDERING_DATE <= #{endDate}
GROUP BY TOR.ORDER_ID , TOR.ORDER_ORDERING_DATE, TOR.ORDER_DEAD_LINE_DATE , TON.ORG_NAME ) TB ORDER BY TB.ORG_NAME, TB.ORDER_ORDERING_DATE , TB.ORDER_DEAD_LINE_DATE LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(TOR.ORDER_ID) FROM TB_ORDER TOR WHERE 1=1 AND TOR.USE_YN = 'Y'
AND TOR.ORDER_ORG_ID = #{orgId}
AND TOR.ORDER_ORDERING_DATE >= #{startDate}
AND TOR.ORDER_ORDERING_DATE <= #{endDate}

mapper.insert:
INSERT INTO TB_ORDER(
        ORDER_ID
        , ORDER_ORG_ID
        , USE_YN
        , ORDER_ORDERING_DATE
        , ORDER_DEAD_LINE_DATE
        , CREATE_DATE
        , CREATE_USER_ID )
VALUES (
           #{orderId}
           , #{orgId}
           , 'Y'
           , #{orderOrderingDate}
           , #{orderDeadLineDate}
           , NOW()
           , #{createUserId}
       )

INSERT INTO TB_PRODUCT(
        PRODUCT_ID
        , ORDER_ID
        , PRODUCT_STYLE_NO
        , PRODUCT_ITEM
        , PRODUCT_SIZE
        , PRODUCT_COLOR
        , PRODUCT_QTY
        , PRODUCT_PRICE
        , PRODUCT_ETC
        , PRODUCT_SORT
        , USE_YN
        , CREATE_DATE
        , CREATE_USER_ID
)VALUES (
         #{productId}
         , #{orderId}
         , #{productStyleNo}
         , #{productItem}
         , #{productSize}
         , #{productColor}
         , #{productQty}
         , #{productPrice}
         , #{productEtc}
         , #{productSort}
         , 'Y'
         , NOW()
         , #{createUserId}
        )

mapper.update:
UPDATE TB_ORDER
SET
    ORDER_ORG_ID = #{orgId}
    , ORDER_ORDERING_DATE = #{orderOrderingDate}
    , ORDER_DEAD_LINE_DATE = #{orderDeadLineDate}
    , UPDATE_ID = #{updateUserId}
    , UPDATE_DATE = NOW()
WHERE
    ORDER_ID = #{orderId}

UPDATE TB_PRODUCT
SET
    PRODUCT_STYLE_NO = #{productStyleNo}
    , PRODUCT_ITEM = #{productItem}
    , PRODUCT_SIZE = #{productSize}
    , PRODUCT_COLOR = #{productColor}
    , PRODUCT_QTY = #{productQty}
    , PRODUCT_PRICE = #{productPrice}
    , PRODUCT_ETC = #{productEtc}
    , UPDATE_DATE = NOW()
    , UPDATE_ID = #{createUserId}
WHERE
    PRODUCT_ID = #{productId}
