mapper.select:
SELECT DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%mì›”') MONTH_PART , TOR.ORDER_ID , TOR.ORDER_ORDERING_DATE , TOR.ORDER_DEAD_LINE_DATE , TP.PRODUCT_ID , TP.PRODUCT_STYLE_NO , TP.PRODUCT_ITEM , TP.PRODUCT_SIZE , TP.PRODUCT_COLOR , TP.PRODUCT_QTY , TP.PRODUCT_PRICE , TP.PRODUCT_ETC , CAST(TP.PRODUCT_PRICE as unsigned) * cast(TP.PRODUCT_QTY as unsigned) * 1.1 TOTAL_PRD_PRC FROM TB_PRODUCT TP LEFT JOIN TB_ORDER TOR ON TOR.ORDER_ID = TP.ORDER_ID WHERE 1=1 AND TP.USE_YN = 'Y' AND TOR.ORDER_ORG_ID = #{orgId} AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%Y') = #{year}
AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%m') >= #{startMonth}
AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%m') <= #{endMonth}
ORDER BY TOR.ORDER_ORDERING_DATE, TP.PRODUCT_STYLE_NO

SELECT TON.ORG_ID , TON.ORG_NAME FROM TB_ORDER TOR LEFT JOIN TB_ORGANIZATION TON ON TON.ORG_ID = TOR.ORDER_ORG_ID WHERE 1=1 AND TOR.ORDER_ORG_ID = #{orgId} AND TOR.USE_YN = 'Y' AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%Y') = #{year}
AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%m') >= #{startMonth}
AND DATE_FORMAT(TOR.ORDER_DEAD_LINE_DATE,'%m') <= #{endMonth}
GROUP BY TON.ORG_ID, TON.ORG_NAME
